import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-add-adventure',
  templateUrl: './add-adventure.component.html',
  styleUrls: ['./add-adventure.component.css']
})
export class AddAdventureComponent implements OnInit {

  adventure = {
    date: '',
    category: '',
    mileage: null,
    estimated_calories: null
  };

  // Inject HttpClient into your component or service.
  constructor(private http: HttpClient) {}

  ngOnInit(): void {}

  onSubmit(): void {
    // Replace with your backend API endpoint
    const apiUrl = 'http://localhost:5000/add_adventure';
    this.http.post(apiUrl, this.adventure).subscribe(response => {
      console.log(response);  // Handle response from your backend API
    }, error => {
      console.error(error);  // Handle error response
    });
  }
}
<div class="container">
  <h2>Add Adventure</h2>

  <form (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="date">Date:</label>
      <input type="date" id="date" [(ngModel)]="adventure.date" name="date" class="form-control" required>
    </div>

    <div class="form-group">
      <label for="category">Category:</label>
      <input type="text" id="category" [(ngModel)]="adventure.category" name="category" class="form-control" required>
    </div>

    <div class="form-group">
      <label for="mileage">Mileage:</label>
      <input type="number" id="mileage" [(ngModel)]="adventure.mileage" name="mileage" class="form-control" required>
    </div>

    <div class="form-group">
      <label for="calories">Estimated Calories:</label>
      <input type="number" id="calories" [(ngModel)]="adventure.estimated_calories" name="calories" class="form-control" required>
    </div>

    <button type="submit" class="btn btn-primary">Add Adventure</button>
  </form>
</div>
